INSERT INTO `mission_talk` VALUES (3,93,'MP=媛媛别闹了，你怎么了\nMB=不开心，超生气的\nMP=冷静，你被阴影影响了\nMB=要你多管\n\nWP=媛媛，你冷静一点，不要冲动！\nWB=我…烦死了！\nWP=你被阴影影响了！\nWB=要你多管！',1,10),(5,27,'MP=媛媛，注意辅助我\nMZ=嗯\n\nWP=媛媛，注意辅助我\nWZ=嗯',1,16),(6,45,'MB=让我生气后果很严重\nMB=小的们，给我上\nMP=小心，金蟾王已经被阴影控制了\n\nWB=让我生气后果很严重\nWB=小的们，给我上\nWP=小心，金蟾王已经被阴影控制了',1,26),(7,54,'MZ=烂泥都能成精，这世界没道理了\nMB=你看起来最好吃\nMB=我一定留到最后再吃\nMZ=做梦\n\nWZ=烂泥都能成精，这世界没道理了\nWB=小姑娘，你看起来最好吃\nWB=我一定留到最后再吃\nWZ=做梦',1,30),(8,63,'MB=小的们，把他们烤熟了带回去\nMY=大白天的打灯笼上厕所\nMB=什么意思？\nMY=找死啊\n\nWB=小的们，把他们烤熟了带回去\nWY=大白天的打灯笼上厕所\nWB=什么意思？\nWY=找死啊',1,61),(9,72,'MB=我要把你们烧成灰烬\nMP=变成灰烬前能问个问题吗？\nMB=问吧，可怜虫\nMP=最近有人来过这里吗？\nMB=没有，你们是第一个\nMP=好吧，那我们可以走吗？\nMB=不可以，受死吧\n\nWB=我要把你们烧成灰烬\nWP=变成灰烬前能问个问题吗？\nWB=问吧，可怜虫\nWP=最近有人来过这里吗？\nWB=没有，你们是第一个\nWP=好吧，那我们可以走吗？\nWB=不可以，受死吧',1,39),(10,87,'MB=想过留剑\nMP=没门\n\nWB=想过留剑\nWP=没门',1,63),(11,90,'MB=年轻人，用你手中的剑\nMB=证明你的剑道吧\nMP=得罪了，前辈\n\nWB=年轻人，用你手中的剑\nWB=证明你的剑道吧\nWP=得罪了，前辈',1,46),(15,315,'MP=啊，晓芸妹妹\nMP=大家小心别伤到晓芸妹妹\n\nWP=啊，晓芸妹妹\nWP=大家小心别伤到晓芸妹妹',2,93),(16,318,'MP=仙子，得罪了\nMB=尽管来吧\n\nWP=仙子，得罪了\nWB=尽管来吧',2,95),(17,319,'MC=不要生气，你听我说…\nMB=别念了…\n\nWC=不要生气，你听我说…\nWB=别念了…',2,96),(18,301,'MB=小子，天堂有路你不走\nMB=地狱无门闯进来\nMP=废话少说，龙姬在哪？\nMB=想知道就打赢我\n\nWB=小丫头，天堂有路你不走\nWB=地狱无门闯进来\nWP=废话少说，龙姬在哪？\nWB=想知道就打赢我',2,90),(19,311,'MB=你们怎么进来的？\nMB=来人啊！\nMP=叫破喉咙也没用！\nMP=受死吧\n\nWB=你们怎么进来的？\nWB=来人啊！\nWP=你猜我们怎么进来的～\nWP=受死吧',2,91),(20,314,'MP=太好了，今天加菜\nMB=唬呲～\n\nWP=太好了，今天加菜\nWB=唬呲～',2,176),(21,323,'MP=小心哦，大叔的墨画咒术很厉害的\n\nWP=小心哦，大叔的墨画咒术很厉害的',2,103),(22,325,'MP=我们还不需要超渡\nMB=下地狱的人都有罪\n\nWP=我们还不需要超渡\nWB=下地狱的人都有罪',2,104),(23,327,'MB=哼，就凭你们也想赢我\nMP=不试试怎么知道行不行\nMB=好，那就先干掉你们\nMB=再找奸奇算账\n\nWB=哼，就凭你们也想赢我\nWP=不试试怎么知道行不行\nWB=好，那就先干掉你们\nWB=再找奸奇算账',2,106),(24,330,'MB=大侠，请手下留情哈\nMB=小的弱不经风的\nMP=哼，胡言乱语\nMB=哈哈\n\nWB=大侠，请手下留情哈\nWB=小的弱不经风的\nWP=哼，胡言乱语\nWB=哈哈',2,107),(25,406,'MP=吃饭睡觉打豆豆～\nMB=气死本帅也～看招！\n\nWP=吃饭睡觉打豆豆～\nWB=气死本帅也～看招！',4,177),(26,407,'MB=武道之途殊途同归，来吧\nMP=前辈！得罪了\n\nWB=武道之途殊途同归，来吧\nWP=前辈！得罪了',4,190),(27,408,'MP=桃花源的树成精还好看些\nMP=这只太丑了\n\nWP=桃花源的树成精还好看些\nWP=这只太丑了',4,198),(28,409,'MB=你们休想夺走我的剑！\n\nWB=你们休想夺走我的剑！',4,205),(29,410,'ZYY=打完树妖打石头灵气太重也不是好事啊',4,213),(30,413,'MB=我要变得更强！变得更强\nMB=我来帮你实现你的大侠梦吧！哈哈哈\nMP=我更拥有战胜自己的力量！\n\nWB=我要变得更强！变得更强\nWB=我来帮你实现你的大侠梦吧！哈哈哈\nWP=我更拥有战胜自己的力量！',4,235),(31,414,'MP=徐福在哪？\nMB=竟敢以下犯上，死吧！\n\nWP=徐福在哪？\nWB=竟敢以下犯上，死吧！',4,243),(32,417,'MB=几天不见倒是变强不少\nMB=可惜蝼蚁始终难逃被踩死的命运\n\nWB=几天不见倒是变强不少\nWB=可惜蝼蚁始终难逃被踩死的命运',4,249),(33,501,'MP=他被阴影附身了！\r\nMP=大家小心\r\nMB=我的美人在哪里\r\n\r\nWP=他被阴影附身了！\r\nWP=大家小心\r\nWB=我的美人在哪里',5,586),(42,502,'MB=小哥你一点都不懂得怜香惜玉\r\nMP=少废话！\r\n\r\nWB=一点都不懂得怜香惜玉\r\nWP=少废话！',5,593),(43,503,'MB=吹飞你们！\r\nWB=吹飞你们！',5,602),(44,504,'MB=你们还真是阴魂不散\r\nMB=这次算你们厉害\r\nMP=不会让你们得逞的！\r\n\r\nWB=你们还真是阴魂不散\r\nWB=这次算你们厉害\r\nWP=不会让你们得逞的！',5,609),(45,505,'MB=小看我的毒！\r\nMB=你们会后悔的！\r\n\r\nWB=小看我的毒！\r\nWB=你们会后悔的！',5,617),(47,506,'MB=与我为敌\r\nMB=掂量过自己的实力吗？\r\n\r\nWB=与我为敌\r\nWB=掂量过自己的实力吗？',5,622),(48,507,'MB=其实我不想为难你们\r\nMP=不会再听你胡言乱语！\r\n\r\nWB=其实我不想为难你们\r\nWP=不会再听你胡言乱语！',5,630),(49,508,'',5,636),(50,849,'MP=小心别伤到唐卿\r\n\r\nWP=小心别伤到唐卿',7,1100),(51,842,'MB=唐卿…唐家堡弟子…都要跟我走…\r\n\r\nWB=唐卿…唐家堡弟子…都要跟我走…',7,1030),(52,878,'MP=住手！\r\nMB=又是你们！\r\n\r\nWP=住手！\r\nWB=又是你们！',7,1047),(53,904,'MB=贼人！看剑！\r\nYMZ=哇哇！这什么情况！\r\n\r\nWB=贼人！看剑！\r\nYMZ=哇哇！这什么情况！',7,1056),(54,924,'MB=我不入地狱，谁入地狱！\r\n\r\nWB=我不入地狱，谁入地狱！',7,1076),(55,947,'MP=为什么要帮助魔头\r\nMB=我想干什么就干什么\r\nMB=你管我？\r\n\r\nWP=为什么要帮助魔头\r\nWB=我想干什么就干什么\r\nWB=你管我？',7,1092);
